  #{templateNo,jdbcType=CHAR}, #{templateMirrorNo,jdbcType=CHAR}, #{capitalCode,jdbcType=VARCHAR}, 
  #{capitalName,jdbcType=VARCHAR}, #{capitalMemberNo,jdbcType=CHAR}, #{capitalStatus,jdbcType=TINYINT}, 
  #{createdTime,jdbcType=TIMESTAMP}, #{modifiedTime,jdbcType=TIMESTAMP}, #{operaterName,jdbcType=CHAR}
  )
