mapper.insert:

insert into t_csc_merchant_member (f_merchant_type, f_merchant_code, f_merchant_no, 
  f_member_name, f_member_type, f_member_no, 
  f_created_time, f_modified_time, f_allow_pre_settle, 
  f_member_fullname, f_created_user, f_modified_user
  )
values (#{merchantType,jdbcType=TINYINT}, #{merchantCode,jdbcType=VARCHAR}, #{merchantNo,jdbcType=VARCHAR}, 
  #{memberName,jdbcType=VARCHAR}, #{memberType,jdbcType=TINYINT}, #{memberNo,jdbcType=CHAR}, 
  #{createdTime,jdbcType=TIMESTAMP}, #{modifiedTime,jdbcType=TIMESTAMP}, #{allowPreSettle,jdbcType=CHAR}, 
  #{memberFullname,jdbcType=VARCHAR}, #{createdUser,jdbcType=VARCHAR}, #{modifiedUser,jdbcType=VARCHAR}
  )

mapper.select:
select

from t_csc_merchant_member
where f_merchant_type = #{merchantType} and f_merchant_code = #{merchantCode}
